mapper.select:
SELECT * FROM posts
WHERE category = #{category}
ORDER BY p.generated_date desc

mapper.update:
UPDATE posts
SET title =
#{title}, content = #{content}, modified_date = (CURRENT_TIMESTAMP),
category = #{category}
WHERE post_id = #{post_id}

UPDATE posts
SET view_cnt = view_cnt + 1
WHERE post_id = #{post_id};
